<?php//// Конттроллер страницы чтения.//include_once('m/M_User.php');class C_User extends C_Base{	//	// Конструктор.	//    public function action_auth(){        $user = new M_User();        if($this->isPost())        {            $login = $_POST['user_name'];            $pass = $_POST['pwd'];            if($user->auth($login, $pass)){                return true;            }else{                echo 'User not exists or something goes wrong. Please try one more time';            }        }    }    public function action_reg(){        $this->title .= '::Registration';        $user = new M_User();        if($this->isPost())        {            $login = $_POST['user_name'];            $pass = $_POST['pwd'];            if($user->reg($login, $pass)){                return true;            }else{            echo 'User not exists or something goes wrong. Please try one more time';            }        }        $this->content = $this->Template('v/v_registration.php', array('title' =>  $this->title));    }    public function action_logout(){        $this->title .= '::Не Залогирован';        $user = new M_User();        if ($user->logout()){            $text = 'You are logged successfully';        }else{            $text = 'Something goes wrong. Please try one more time';        }        $text = text_get();        $this->content = $this->Template('v/v_index.php', array('text' => $text));    }}